package com.jobPortal.bean;

public class SearchFilterForCompany {

	private String email;
	private double ssc_marks;
	private double hsc_marks;
	private double diploma_marks;
	private double be_marks;

	public SearchFilterForCompany() {
		super();
		// TODO Auto-generated constructor stub
	}

	public String getEmail() {
		return email;
	}

	public void setEmail(String email) {
		this.email = email;
	}

	public double getSsc_marks() {
		return ssc_marks;
	}

	public void setSsc_marks(double ssc_marks) {
		this.ssc_marks = ssc_marks;
	}

	public double getHsc_marks() {
		return hsc_marks;
	}

	public void setHsc_marks(double hsc_marks) {
		this.hsc_marks = hsc_marks;
	}

	public double getDiploma_marks() {
		return diploma_marks;
	}

	public void setDiploma_marks(double diploma_marks) {
		this.diploma_marks = diploma_marks;
	}

	public double getBe_marks() {
		return be_marks;
	}

	public void setBe_marks(double be_marks) {
		this.be_marks = be_marks;
	}

}
